import { Colors } from '@/constants/Colors';
import { Design } from '@/constants/Design';
import { useColorScheme } from '@/hooks/useColorScheme.web';
import { StyleProp, ViewStyle } from 'react-native';
import Svg, { ClipPath, Defs, G, Path, Rect } from 'react-native-svg';

export type IconProps = {
	name:
		| 'chevron-top'
		| 'chevron-right'
		| 'chevron-down'
		| 'chevron-left'
		| 'arrow-up'
		| 'arrow-down'
		| 'arrows-up-down'
		| 'pen'
		| 'user'
		| 'users-group'
		| 'calendar'
		| 'check-list'
		| 'door-open'
		| 'tweaks'
		| 'plus'
		| 'close'
		| 'checkmark'
		| 'chat-cloud'
		| 'trash'
		| 'sort-list'
		| 'megaphone'
		| 'loader';
};

export default function Icon({
	name,
	...iconProps
}: IconProps & Icons.IconProps) {
	const colorScheme = useColorScheme();

	iconProps = {
		...iconProps,
		size: iconProps.size ?? Design.icon.small,
		color: iconProps.color ?? Colors[colorScheme].text[100],
	};

	switch (name) {
		case 'chevron-top':
			return <Icons.ChevronTop {...iconProps} />;
		case 'chevron-right':
			return <Icons.ChevronRight {...iconProps} />;
		case 'chevron-down':
			return <Icons.ChevronDown {...iconProps} />;
		case 'chevron-left':
			return <Icons.ChevronLeft {...iconProps} />;
		case 'arrow-up':
			return <Icons.ArrowUp {...iconProps} />;
		case 'arrow-down':
			return <Icons.ArrowDown {...iconProps} />;
		case 'arrows-up-down':
			return <Icons.ArrowsUpDown {...iconProps} />;
		case 'pen':
			return <Icons.Pen {...iconProps} />;
		case 'user':
			return <Icons.User {...iconProps} />;
		case 'users-group':
			return <Icons.UsersGroup {...iconProps} />;
		case 'calendar':
			return <Icons.Calendar {...iconProps} />;
		case 'check-list':
			return <Icons.CheckList {...iconProps} />;
		case 'door-open':
			return <Icons.DoorOpen {...iconProps} />;
		case 'tweaks':
			return <Icons.Tweaks {...iconProps} />;
		case 'plus':
			return <Icons.Plus {...iconProps} />;
		case 'close':
			return <Icons.Close {...iconProps} />;
		case 'checkmark':
			return <Icons.Checkmark {...iconProps} />;
		case 'chat-cloud':
			return <Icons.ChatCloud {...iconProps} />;
		case 'trash':
			return <Icons.Trash {...iconProps} />;
		case 'sort-list':
			return <Icons.SortList {...iconProps} />;
		case 'megaphone':
			return <Icons.Megaphone {...iconProps} />;
		case 'loader':
			return <Icons.Loader {...iconProps} />;
		default:
			return null;
	}
}

